import React from 'react';
import {Link} from 'react-router-dom';
import styled from 'styled-components';

const Button = styled.div`
    font-size: 16px;
    vertical-align: text-bottom;
    font-family: Raleway, sans-serif;
    font-weight: 500;
    padding-top: 8px;
    padding-left: 15px;
    padding-right: 15px;
`

function NavigationButton(props){
    const parseNextPageLink = () => {
        if (props.nextPage.includes('everything')){
            return '/catalog?view=all'
        } else if (props.nextPage.includes('explore')){
            return `/catalog?view=${props.exploreCategory}`
        } else {
            return props.nextPage.replace(/\W/g, '')
        }
    };
    // const parseDisplayTest = () =>{
    //     if (props.nextPage.includes('explore')){
    //         return 'explore' + <i class="fas fa-caret-down"></i>;
    //     } else {
    //         return props.nextPage;
    //     }
    // };
    // const displayText = parseDisplayTest();
    const nextPage = parseNextPageLink();
    return (
        <Button>
            props.nextPage.includes('explore') ?
            <Link style={{textDecoration:'None' , color:'black'}} to={nextPage}>{props.nextPage}<i
                className="fas fa-caret-down"></i></Link>
            : <Link style={{textDecoration:'None' , color:'black'}} to={nextPage}>{props.nextPage}</Link>;
        </Button>
    )
}

export default NavigationButton;
