import React from 'react';
import Navbar from './navbar';
import styled from 'styled-components';
import '../css/about.css';
import AboutMainGraphic from "./aboutmaingraphic";
import {motion} from "framer-motion";

const AboutContainer = styled.div`
    height: 100vh;
    background-color: #EFE7E2;
`
const AboutHeadline = styled.div`
    font-family: Ramaraja;
    font-size: 80px;
`

const MainWrapper = styled.div`
    display: grid;
    grid-gap: 1rem;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    padding-left: 6rem;
    padding-right: 6rem;
`

const AboutMessage = styled.div`
    grid-column: auto / span 2;
`

const AboutBody = styled.div`
    font-family: Raleway;
    font-size: 16px;
    font-weight: 500;
`

const GitHubLink = styled.a`
    text-decoration: none;
    color: black;
    hover: 
`

function About (props){
    return(
        <AboutContainer>
            <Navbar/>
            <motion.div key="home" variants={props.variants} transition={props.transition} initial="pageInit" animate="pageIn" exit="pageOut">
            <MainWrapper>
                <AboutMessage>
                    <AboutHeadline>
                        About
                    </AboutHeadline>
                    <AboutBody>
                        DataStructs is a beautiful, intuitive platform for vizualizing data structures and algorithms.<br/>
                        <br/>
                        DataStructs was engineered around the philosophy of <em>treating every day like it's Karen's last ziti.</em>
                        <br/><br/>
                        As of May 2020, DataStructs is built around React, animated with Framer Motion, and hosted on AWS Amplify.
                        <br/><br/>
                        <GitHubLink className="hoverHighlit" href='https://github.com/conormccormack/datastructs'>
                            <i className="fab fa-github"></i> View this project on <strong>Github</strong>
                        </GitHubLink> @conormccormack/datastructs
                    </AboutBody>
                </AboutMessage>
                <AboutMainGraphic/>
            </MainWrapper>
            </motion.div>
        </AboutContainer>
    )
}

export default About;
