import React, {useEffect, useState} from "react";
import styled from 'styled-components';
import { motion } from 'framer-motion';
import Navbar from "./navbar";
import BinaryTree from './binarytree'

const TestSandbox = styled.div`
    height: 100vh;
    background-color: #EFE7E2;
    grid-template-columns: 1fr 1fr;
    display: grid;
`

const ListWrapper = styled.div`
    height: 200px; 
    width: 100px;
    margin-left: 100px;
    margin-right: 100px;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr 1fr;
`

const DisplayNode = styled.div`
    background-color: #D8BBFF; 
    border-radius: 50%;
    margin: 20px;
    height: 100px; 
    width: 100px;
    margin: auto;
    display: flex;
    justify-content: center;
    align-items: center;
`

const NodeContent = styled.div`
    text-align: center;
    vertical-align: middle;
    font-size: 30px;
    font-family: open-sans;

`

const Margin20 = styled.div`
    margin: 20px;
`

class Node {
    constructor(data, left = null, right = null) {
        this.data = data;
        this.leftChild = left;
        this.rightChild = right;
    }
}

function TestAnimation(){
    const [inputValue, setInputValue ] = useState([]);
    const [Tree, setTree] = useState([
        {
            data: 10,
            left: {
                data: 2,
                left: null,
                right: null,
            },
            right: {
                data: 12,
                left: null,
                right: null,
            },
        }
    ]);


    const onInsertDown = event => {
        event.preventDefault();
        setTree({data : inputValue})
    };

    const handleChange = event => setInputValue(event.target.value);

    return (
        <div>
            <Navbar/>
            <TestSandbox>
                <div>
                    <ul>
                        <li>{Tree[0].data}</li>
                    </ul>
                </div>
                <form style={{paddingLeft:'100px'}} onSubmit={onInsertDown}>
                    <input type='text' value={parseInt(inputValue)} onChange={handleChange}></input>
                    Insert
                </form>
            </TestSandbox>
        </div>
    )

}

export default TestAnimation;
