import React, { useState } from "react";
import styled from 'styled-components';
import { motion } from 'framer-motion';
import Navbar from "./navbar";


const TestSandbox = styled.div`
    height: 100vh;
    background-color: #EFE7E2;
    display: grid;
`

const Centered = styled.div`
    height: 200px; 
    width: 100px;
    text-align: center;
    display: flex;
`

const Box = styled.div`
    background-color: #D8BBFF; 
    border-radius: 50%;
    margin-top: 10px;
    margin-left: 400px;
    margin-right: 20px;
    height: 100px; 
    width: 100px;
    margin: auto;
`

const Input = () => {
    return <input placeholder="Your input here" />;
};



function TestAnimation(){
    const [nodeList, setNodeList] = useState([]);


    const onAddBoxClick = event => {
        setNodeList(nodeList.concat(<motion.div whileHover={{ scale: 3}} >
            <Box/>
        </motion.div>));
    };

    return (
        <div>
            <Navbar/>
            <TestSandbox>
                <button onClick={onAddBoxClick}>Add a node</button>
                <Centered>
                    {nodeList}
                    <motion.div whileHover={{ scale: 3}} >
                           <Box/>
                    </motion.div>
                </Centered>
            </TestSandbox>
        </div>
    )

}

export default TestAnimation;
