import React from 'react';
import {AnimatePresence, motion} from 'framer-motion';
import styled from "styled-components";
import '../css/bst.css'

const NodeContent = styled.div`
    text-align: center;
    vertical-align: middle;
    font-size: 20px;
    font-family: Helvetica;
    color: #FFF;
`

function BSTNode (props) {
    return(
        <motion.div className="bstnode" key={props.key} style={{float: 'left'}} exit={{ scale: 0}} initial={{ scale: 0}}
                     animate={{ scale: 1, x: props.shift, y: props.shift}} whileHover={{scale: 1.25}}>
                <NodeContent>{props.data}</NodeContent>
        </motion.div>
    )
}

export default BSTNode;
