                    10
                </motion.div>
                <motion.div className="bstnode" style={{float: 'left'}} exit={{ scale: 0}} initial={{ scale: 0}}
                            animate={{ scale: 1, x: props.shift, y: props.shift}} whileHover={{scale: 1.25}}>
                    10}
                </motion.div>
            </AnimatePresence>
        </motion.div>
    )

}

export default BinaryTree;
