function TestAnimation(){
    const [numberNodes, setNumberNodes] = useState(0);
    const [inputValue, setInputValue ] = useState([]);
    const [treeList, setTreeList] = useState({
        data: 10,
        left: {
            data: 8,
            left: {},
            right: {},
        },
        right: {
            data: 16,
            left: {},
            right: {},
        }
    });

    const [tree, setTree] = useState(
        {
            data: 10,
            left: {
                data: 8,
                left: {},
                right: {},
            },
            right: {
                data: 16,
                left: {},
                right: {},
            }
        }
    );



    const onInsertDown = event => {
        event.preventDefault();
        if (numberNodes === 0){
            setTree(
                {
                    data: inputValue,
                    left: {},
                    right: {},
                }
            )
        }
        setNumberNodes(numberNodes => numberNodes + 1);
        const treenum = {tree, numberNodes};
        console.log(treenum)
    };

    const handleChange = event => setInputValue(event.target.value);

    return (
        <div>
            <Navbar/>
            <TestSandbox>
                <ListWrapper>
                    <Margin20>
                        { BinaryTree(tree)}
                    </Margin20>
                </ListWrapper>
                <form style={{paddingLeft:'100px'}} onSubmit={onInsertDown}>
                    <label>
                        <input type='text' value={inputValue} onChange={handleChange}></input> Input
                    </label>
                </form>
            </TestSandbox>
        </div>
    )
}

// Recursion
function BinaryTreeList ( tree ) {
    return(
        <div>
            { tree.map(node => (
                <ul>
                    <Margin20>
                        <motion.div initial={{scale: 0}} animate={{scale: 1}} whileHover={{scale: 1.5}}>
                            <DisplayNode>
                                <NodeContent>{node.data}</NodeContent>
                            </DisplayNode>
                        </motion.div>
                    </Margin20>

            { node.left != null && BinaryTree(tree[0]) }
                </ul>
            ))}
        </div>
    )

}

function BinaryTree (tree) {

    return(
        <div>
            <ul>
                <Margin20>
                    <motion.div initial={{scale: 0}} animate={{scale: 1}} whileHover={{scale: 1.5}}>
                        <DisplayNode>
                            <NodeContent>{tree.data}</NodeContent>
                        </DisplayNode>
                    </motion.div>
                </Margin20>
            </ul>
        </div>
    )

}

export default TestAnimation;
