import React, { Component } from 'react';
import anime from 'animejs';
import '../css/bst.css';
import styled from 'styled-components';

const PageWrapper = styled.div`
    padding: 40px;
    background-color: #EFE7E2;
    height: 100vh;
    display: grid;
    grid-template-columns: 1fr 1fr;
`

const Controls = styled.div`

`

const NodeContainer = styled.div`
`

class BinarySearchTree {
    constructor() {
        this.root = null;   
    }

    insert(value) {
        var newNode = new Node (value);
        if (this.root === null) this.root = newNode;
        else this.insertNode(this.root, newNode);
    }
    insertNode(root, newNode){ 
        if (newNode.value < root.value){
            if (root.left !== null) this.insertNode(root.left, newNode);
            else root.left = newNode;
        } else if (newNode.value >= root.value){
            if (root.right !== null) this.insertNode(root.right, newNode);
            else root.right = newNode;
        }
    }
}

class Node {
    constructor (value){
        this.value = value;
        this.left = null;
        this.right = null;
    }
}


function addNode(value) {
    var node = document.createElement("div");
    node.setAttribute('class', 'bstnode')
    var text = document.createTextNode(value);
    node.appendChild(text);
    document.getElementById("nodecontainer").appendChild(node);
}

async function animate() {
    anime({
        targets: '#container > div',
        keyframes: [
            {translateX: 0},
            {translateY: 500},
            {translateX: 800, translateY: 0},
            {translateY: 500},
            {translateX: 0, translateY: 0},
        ],
        duration: 4000,
    });

}

class AnimeTest extends Component {
    constructor (props) {
        super(props);
        this.state = {
            value: '',
            bst: new BinarySearchTree,
        };
        this.handleInputSubmit = this.handleInputSubmit.bind(this);
        this.handleInputChange = this.handleInputChange.bind(this);
    }

    handleInputChange(event) {
        this.setState({ value: event.target.value });
    }

    handleInputSubmit(event) {
        event.preventDefault();
        this.state.bst.insert(this.state.value);
        this.setState({ value: ''});
        console.log(this.state.bst);
    }

    shouldComponentUpdate(){
        return false;
    }

    componentDidMount(){

    }


    render(){ 
        return(
            <PageWrapper>
                <NodeContainer id="nodecontainer">

                </NodeContainer>
                <Controls>
                    // Uncontrolled form
                    <form onSubmit={this.handleInputSubmit}>
                        <label>
                            <input defaultValue="0" type="number" onChange={this.handleInputChange}/> Input
                        </label>
                    </form>
                </Controls>
            </PageWrapper>
        );
    }
}

export default AnimeTest;
