import React, {useEffect, useState} from 'react';
import {AnimatePresence, motion} from 'framer-motion';
import styled from 'styled-components';
import BSTNode from "./bstnode";

const Flex = styled.div`
    display: flex;
`

const FillWidth = styled.div`
    width: 100%;
    height: 100%;
`

// Build and maintain state as array.
// Each element in array represents a level on the tree.

function BinaryTree (props){
    const [tree, setTree] = useState(props.BST.filter(node => node.data !== null).map((node) =>{
        const indexOnLevel = computeIndexOnLevel(node.id, node.level);
        const shift = indexOnLevel % 2 === 0 ? indexOnLevel*(-20) : indexOnLevel * 20;
        return (
            <BSTNode data={node.data} key={node.id} shift={shift}></BSTNode>
        )
    }));
    // Return level on tree given id
    const computeLevel = id => (Math.floor(Math.log2(id + 1)));
    // Compute left-right zero based index of id on its level in the tree.
    const computeIndexOnLevel = (id, level) => (id + 1 - Math.pow(2,level));

    return(
        <motion.div>
            {JSON.stringify(props.BST)}
            <AnimatePresence>
            {JSON.stringify(props.BST)}
            </AnimatePresence>
        </motion.div>
    )

}

export default BinaryTree;
