function TestAnimation(){
    const [numberNodes, setNumberNodes] = useState(0);
    const [inputValue, setInputValue ] = useState([]);
    // const [tree, setTree] = useState({});
    const [tree, setTree] = useState(
        {
            data: 10,
            left: {
                data: 8,
                left: {
                    data: 5,
                    left: null,
                    right: null,
                },
                right: {
                    data: 9,
                    left: null,
                    right: null,
                },
            },
            right: {
                data: 16,
                left: {
                    data: 12,
                    left: null,
                    right: null,
                },
                right: {
                    data: 18,
                    left: null,
                    right: null,
                },
            }
        }
    );


    class Node {
        constructor(data)
        {
            this.data = data;
            this.left = null;
            this.right = null;
        }
    }

    class BinarySearchTree {

        insert(data) {
            const newNode = new Node(data);
            this.insertNodeToTree(this.root, newNode);
        }

        insertNodeToTree(node, newNode){
            if (newNode.data < node.data){
                if (node.left == null) node.left = newNode;
                else this.insertNodeToTree(node.left, newNode);
            } else {
                if (node.right == null) node.right = newNode;
                else this.insertNodeToTree(node.right, newNode);
            }
        }

        constructor(tree){
            this.root = new Node(tree.data, tree.left, tree.right);
            if (tree.left != null) this.insertNodeToTree(this.root, tree.left);
            if (tree.right != null) this.insertNodeToTree(this.root, tree.right);
        }

    }

    const insertNode = (tree) => {
        let newTree = new BinarySearchTree(tree);
        newTree.insert(inputValue);
        setTree(newTree);

        // let newTree = tree;
        // console.log(newTree);
        // if (inputValue >= newTree.data){
        //     // Base case: create leaf node.
        //     if (newTree.right == null){
        //         newTree.right = {
        //             data: inputValue,
        //             left: null,
        //             right: null,
        //         };
        //         return newTree;
        //     }
        //     else {
        //         return insertNode(newTree.right);
        //     }
        // } else {
        //     // Base case: create leaf node.
        //     if (newTree.left == null){
        //         newTree.left = {
        //             data: inputValue,
        //             left: null,
        //             right: null,
        //         };
        //         return newTree;
        //     } else {
        //         return insertNode(newTree.right);
        //     }
        // }
    };

    const onInsertDown = event => {
        event.preventDefault();
        if (Object.keys(tree).length === 0){
            setTree(
                {
                    data: inputValue,
                    left: null,
                    right: null,
                }
            )
        } else {
            setTree(insertNode(tree));
        }

        setNumberNodes(numberNodes => numberNodes + 1);
    };

    const handleChange = event => setInputValue(event.target.value);
    const { first, second } = ['left', 'left'];



    return (
        <div>
            <Navbar/>
            <TestSandbox>
                <ListWrapper>
                    <Margin20>
                        { Object.keys(tree).length > 0 &&  BinaryTree(tree)}
                    </Margin20>
                </ListWrapper>
                <form style={{paddingLeft:'100px'}} onSubmit={onInsertDown}>
                    <label>
                        <input type='text' value={inputValue} onChange={handleChange}></input> Input
                    </label>
                    <p>Number of Nodes: {numberNodes}</p>
                </form>
            </TestSandbox>
        </div>
    )
}

// Recursion
function BinaryTree (tree) {
    return(
        <div>
            <ul>
                <Margin20>
                    <motion.div initial={{scale: 0}} animate={{scale: 1}} whileHover={{scale: 1.5}}>
                        <DisplayNode>
                            <NodeContent>{tree.data}</NodeContent>
                        </DisplayNode>
                    </motion.div>
                </Margin20>
                { tree.left && BinaryTree(tree.left) }
                { tree.right && BinaryTree(tree.right) }
            </ul>
        </div>
    )
}

export default TestAnimation;
