import React from 'react';
import {AnimatePresence, motion} from 'framer-motion';
import styled from "styled-components";

const DisplayNode = styled.div`
    background: rgb(222,170,255);
    background: linear-gradient(225deg, rgba(222,170,255,1) 34%, rgba(208,209,255,1) 80%);
    border-radius: 50%;
    height: 70px; 
    width: 70px;
    display: flex;
    justify-content: center;
    align-items: center;
`

const NodeContent = styled.div`
    text-align: center;
    vertical-align: middle;
    font-size: 20px;
    font-family: Helvetica;
    color: #FFF;
`

function BSTNode (props) {
    return(
        <AnimatePresence>
            {<motion.div style={{float: 'left'}} initial={{ scale: 0}} animate={{ scale: 1, x: shift, y: shift}} whileHover={{scale: 1.2}}>
                <DisplayNode>
                    <NodeContent>{props.data}</NodeContent>
                </DisplayNode>
            </motion.div>}
        </AnimatePresence>
    )
}

export default BSTNode;
