import React, { useState } from "react";
import styled from 'styled-components';
import { motion } from 'framer-motion';
import Navbar from "./navbar";


const TestSandbox = styled.div`
    height: 100vh;

    background-color: #EFE7E2;
    display: grid;
`

const ListWrapper = styled.div`
    height: 200px; 
    width: 100px;
    margin-left: 100px;
    margin-right: 100px;
    display: flex;
`

const Box = styled.div`
    background-color: #D8BBFF; 
    border-radius: 50%;
    margin: 20px;
    height: 100px; 
    width: 100px;
    margin: auto;
`

const Margin20 = styled.div`
    margin: 20px;
`

function TestAnimation(){
    const [nodeList, setNodeList] = useState([]);


    const onAddBoxClick = event => {
        setNodeList(nodeList.concat(<Margin20><motion.div whileHover={{ scale: 1.5 }} >
            <Box/>
        </motion.div></Margin20>));
    };

    return (
        <div>
            <Navbar/>
            <TestSandbox>
                <button onClick={onAddBoxClick}>Add a node</button>
                <ListWrapper>
                    {nodeList}
                </ListWrapper>
            </TestSandbox>
        </div>
    )

}

export default TestAnimation;
